select *from customer_behavior.customer limit 5;

-- Q1.What is the total revenue generated by male vs female customers?
select gender,sum(purchase_amount) as revenue 
from customer 
group by gender; 

-- Q2.Which customer used a discount but still spend more than the avergae purchase amount?
select customer_id,purchase_amount
from customer 
where discount_applied='Yes' and purchase_amount>= (select avg(purchase_amount) from customer);

-- Q3.Which are the top 5 products with the highest avergae rating review?
select item_purchased,round(avg(review_rating),2) as "Average_review_rating"
from customer
group by item_purchased
order by avg(review_rating) desc
limit 5;

-- Q4.Compare the average purchase amount between standard and express shipping?
select shipping_type,round(avg(purchase_amount),2)
from customer
where shipping_type in ('Standard','Express')
group by shipping_type;

-- Q5.Do subscribed customers spend more?Compare averge spend and total revenue between subscribers and non-subscribers?
 select subscription_status,count(customer_id),avg(purchase_amount),sum(purchase_amount)
 from customer 
 group by subscription_status;
 
 -- Q6.Which 5 products have the highest percentage of purchase with discounts applied?
 select item_purchased,
 round(sum(case when discount_applied='Yes' then 1 else 0 end) /count(*) * 100,2) as discount_rate
 from customer
 group by item_purchased
 order by discount_rate desc
 limit 5;
 
 -- Q7.Segment customer into New,Returning and Loyal based on their total number of previous purchases, and show the count of each segment 
 with customer_type as(
 select customer_id,previous_purchases,
 case 
	when previous_purchases=1 then 'New'
    when previous_purchases between 2 and 10 then 'Returning'
    else 'Loyal'
end as customer_segment 
 from customer 
 )
 select customer_segment,count(*) as 'Number of customers'
 from customer_type
 group by customer_segment ;
 
 -- Q8. What are the top 3 most purchased products within each category
 with item_counts as(
 select category,
 item_purchased,
 COUNT(customer_id) as total_orders,
 row_number() over(partition by category order by count(customer_id) desc) as item_rank
 from customer 
 group by category,item_purchased 
 )
 select item_rank,category,item_purchased,total_orders
 from item_counts
 where item_rank<=3;
 
 -- Q9. Are customers who are repeat buyers(more than 5 previous purchase) also likely to subscribe?
 select subscription_status,
 count(customer_id) as repeat_buyers
 from customer 
 where previous_purchases >5
 group by subscription_status;
 
 -- Q10. What is the revenue cntribution of each age group?
 select sum(purchase_amount) as total_revenue ,age_group
 from customer 
 group by age_group
 order by total_revenue desc;